import React from 'react';
import Loading from './Loading';
import PersonCard from './PersonCard';

class RandomUser extends React.Component {
  constructor() {
    super();
    this.fetchUser = this.fetchUser.bind(this);
    this.getUserElements = this.getUserElements.bind(this);

    this.state = {
      loading: true,
      user: [],
    }
  }

async fetchUser() {
  const requestReturn = await fetch('https://api.randomuser.me/')
  const requestObj = await requestReturn.json()
  this.setState({
    user: requestObj.results,
    loading: false,
  })
}

getUserElements(user) {
  return {
    name: `${user.name.first} ${user.name.last}`,
    email: user.email,
    age: user.dob.age,
    image: user.picture.thumbnail,
  };
}

componentDidMount() {
  this.fetchUser();
}

shouldComponentUpdate(_nextProps, nextState) {
  // console.log(nextState.person[0].dob.age);
  const AGE = 50;
  if (nextState.person[0].dob.age < AGE) return true;
}


  render() {
    const { loading, person } = this.state
    if (loading) {
      return <Loading />
    }

    return(
      <div>
       <PersonCard
        person={ this.getUserElements(person[0]) }
        />
      </div>
    )
  }
}

export default RandomUser;
