class RandomUser extends React.Component {
  constructor() {
    super();
    this.fetchUser = this.fetchUser.bind(this);

    this.state = {
      user: [],
    }
  }

async fetchUser() {
  this.setState(
    {loading: true}, 
    async () => {
  const requestReturn = await fetch('https://api.randomuser.me/')
  const requestObj = await requestReturn.json()
  this.setState({
    user: requestObj.results,
  })
})
}

componentDidMount() {
  this.fetchUser();
}


